import React, { useState, useEffect } from "react";
import axios from "axios";

const CommentSection = ({ blogId, userId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  const fetchComments = () => {
    axios
      .get(`http://localhost:3000/api/comments/${blogId}`)
      .then((response) => {
        console.log('fetch comments response:', response)
        setComments(response.data.comments);
      })
      .catch((error) => {
        console.error("Error fetching comments:", error);
      });
  };

  useEffect(() => {
    fetchComments();
  }, []);

  const postComment = () => {
    if (!newComment) return;
    const requestBody = {
        blogid: blogId.toString(),
        userid: userId.toString(),
        desc: newComment,
      };
    axios
      .post(`http://localhost:3000/api/comments/`, requestBody,{
        withCredentials: true,
      })
      .then((response) => {
        console.log("Post comment response :",response)
        fetchComments();
        setNewComment("");
      })
      .catch((error) => {
        console.error("Error posting comment:", error);
      });
  };

  return (
    <div className="mt-6">
      <h2 className="text-xl font-semibold mb-4">Comments</h2>
      <div className="space-y-4">
        {comments && comments.map((comment) => (
          <div key={comment.id} className="bg-gray-100 p-4 rounded">
            <p className="text-gray-800">{comment.content}</p>
          </div>
        ))}
      </div>
      <div className="mt-4">
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-indigo-500"
          placeholder="Write your comment..."
          rows="4"
        ></textarea>
        <button
          onClick={postComment}
          className="mt-2 px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-500 focus:outline-none"
        >
          Post Comment
        </button>
      </div>
    </div>
  );
};

export default CommentSection;
