import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import CommentSection from "./CommentSection";
const BlogDetail = () => {
  const navigate = useNavigate();
  const [singlePost, setSinglePost] = useState();
  const { id } = useParams();
  var commenterName = "Anonymous"
  useEffect(() => {
    const user = localStorage.getItem("user");
    if (user) {
      const parsedUser = JSON.parse(user);
      commenterName = `${parsedUser.first_name} ${parsedUser.last_name}`;
      console.log("Updated commenterName :", commenterName)
    } else {
      console.log("User is not signed in");
    }

  }, []);

  const singleBlog = () => {
    axios
      .get(
        `http://localhost:3000/api/allpost/${id}`,
        //{},
        {
          withCredentials: true,
        }
      )
      .then(function (response) {
        //setLoading(false);
        //setBlogData(response?.data?.data);
        setSinglePost(response?.data?.data);
        console.log("BlogDetails from /allpost :",response?.data?.data);
      })
      .catch(function (error) {
        // handle error
        //setLoading(false);
        //   setMessage(error?.response?.data?.message);
        //   openSnackbar(error?.response?.data?.message);
        console.log(error);
      })
      .then(function () {
        // always executed
      });
  };
  useEffect(() => {
    singleBlog();
  }, []);
  return (
    <div className="relative">
      <div className="max-w-3xl mb-10 rounded overflow-hidden flex flex-col mx-auto text-center">
        <div className="max-w-3xl mx-auto text-xl sm:text-4xl font-semibold inline-block hover:text-indigo-600 transition duration-500 ease-in-out inline-block mb-2">
        {singlePost?.title}
        </div>

        <img className="w-full h-96 my-4" src={singlePost?.Image} />
        <p className="text-gray-700 text-base leading-8 max-w-2xl mx-auto">
          Author: {singlePost?.user?.first_name} {singlePost?.user?.last_name}
        </p>

        <hr />
      </div>

      <div className="max-w-3xl mx-auto">
        <div className="mt-3 bg-white rounded-b lg:rounded-b-none lg:rounded-r flex flex-col justify-between leading-normal">
          <div className="">
            <p className="text-base leading-8 my-5">{singlePost?.desc}</p>
          </div>
        </div>
      </div>
      {console.log("blogId : " , singlePost?.id)}
      <CommentSection blogId={singlePost?.id} commenterName={commenterName} />
    </div>
  );
};

export default BlogDetail;
