import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import { useSnackbar } from "react-simple-snackbar";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const [message, setMessage] = useState();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const options = {
    position: "bottom-right",
    style: {
      backgroundColor: "gray",
      border: "2px solid lightgreen",
      color: "white",
      fontFamily: "Menlo, monospace",
      fontSize: "20px",
      textAlign: "center",
    },
    closeStyle: {
      color: "lightcoral",
      fontSize: "16px",
    },
  };
  const [openSnackbar, closeSnackbar] = useSnackbar(options);
  const onSubmit = (data) => {
    setLoading(true);
    const body = {
      ...data,
      //phone: parseInt(data.phone),
    };
    axios
      .post(
        `http://localhost:3000/api/login`,
        { ...body },
        {
          withCredentials: true,
        }
      )
      .then(function (response) {
        // handle success
        setLoading(false);
        setMessage(response?.data?.message);
        openSnackbar(response?.data?.message);
        localStorage.setItem("user", JSON.stringify(response?.data?.user));
        console.log("Setting in local storage in signin" ,response?.data?.message);
        navigate("/");

      })
      .catch(function (error) {
        // handle error
        setLoading(false);
        setMessage(error?.response?.data?.message);
        openSnackbar(error?.response?.data?.message);
        //console.log(error?.response?.data?.message);
      })
      .then(function () {
        // always executed
      });

    console.log(data);
  };
  return (
    <div className="bg-gradient-to-r from-cyan-500 to-blue-500 min-h-screen flex justify-center items-center">
      <div className="bg-white w-96 border border-gray-200 rounded-md p-8">
        <h1 className="text-center text-[#0c2650] text-2xl font-bold mb-4">
          Login
        </h1>
        {message && (
          <div className="bg-gradient-to-r from-fuchsia-400 via-sky-400 to-violet-200 text-white font-bold rounded p-2 mb-4">
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4">
            <label className="block text-sm mb-1">Email:</label>
            <input
              type="email"
              name="email"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your Email Address"
              autoComplete="on"
              {...register("email", { required: true })}
            />
            {errors.email && (
              <span className="text-red-600 text-[10px] italic">
                Email is required
              </span>
            )}
          </div>
          <div className="mb-4">
            <label className="block text-sm mb-1">Password:</label>
            <input
              type="password"
              name="password"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your password"
              autoComplete="on"
              {...register("password", { required: true })}
            />
            {errors.password && (
              <span className="text-red-600 text-[10px] italic">
                Password is required
              </span>
            )}
          </div>

          <div className="flex justify-between items-center mb-4">
            <div className="text-sm">
              <Link to="/forgot-password" className="text-blue-500">
                Forgot Password?
              </Link>
            </div>
            <button
              className={`px-4 py-2 bg-blue-500 text-white font-bold rounded hover:bg-blue-700 focus:outline-none`}
              disabled={loading}
            >
              {loading ? "Loading..." : "Login"}
            </button>
          </div>
          <div className="text-center text-sm">
            Don't have an account?{" "}
            <Link to="/register" className="text-blue-500">
              Sign Up
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
