import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import { useSnackbar } from "react-simple-snackbar";
import { Link, useNavigate } from "react-router-dom";

const Register = () => {
  const [message, setMessage] = useState();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const options = {
    position: "bottom-right",
    style: {
      backgroundColor: "gray",
      border: "2px solid lightgreen",
      color: "white",
      fontFamily: "Menlo, monospace",
      fontSize: "20px",
      textAlign: "center",
    },
    closeStyle: {
      color: "lightcoral",
      fontSize: "16px",
    },
  };
  const [openSnackbar] = useSnackbar(options);
  const onSubmit = (data) => {
    setLoading(true);
    const body = {
      ...data,
      //phone: parseInt(data.phone),
    };
    axios
      .post(`http://localhost:3000/api/register`, { ...body })
      .then(function (response) {
        // handle success
        setLoading(false);
        setMessage(response?.data?.message);
        openSnackbar(response?.data?.message);
        localStorage.setItem("user", JSON.stringify(response?.data?.user));
        console.log("Setting in local storage n signup" ,response?.data?.message);
        navigate("/");
      })
      .catch(function (error) {
        // handle error
        setLoading(false);
        setMessage(error?.response?.data?.message);
        openSnackbar(error?.response?.data?.message);
        //console.log(error?.response?.data?.message);
      })
      .then(function () {
        // always executed
      });

    //console.log(data);
  };
  return (
    <div className="bg-gradient-to-r from-cyan-500 to-blue-500 min-h-screen flex justify-center items-center">
      <div className="bg-white w-96 border border-gray-200 rounded-md p-8">
        <h1 className="text-center text-[#0c2650] text-2xl font-bold mb-4">
          Sign up
        </h1>
        {message && (
          <div className="bg-gradient-to-r from-fuchsia-400 via-sky-400 to-violet-200 text-white font-bold rounded p-2 mb-4">
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4">
            <label className="block text-sm mb-1">First Name:</label>
            <input
              type="text"
              name="first_name"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your First Name"
              autoComplete="on"
              {...register("first_name", { required: true })}
            />
            {errors.first_name && (
              <span className="text-red-600 text-[10px] italic">
                First Name is required
              </span>
            )}
          </div>
          <div className="mb-4">
            <label className="block text-sm mb-1">Last Name:</label>
            <input
              type="text"
              name="last_name"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your Last Name"
              autoComplete="on"
              {...register("last_name", { required: true })}
            />
            {errors.last_name && (
              <span className="text-red-600 text-[10px] italic">
                Last Name is required
              </span>
            )}
          </div>
          <div className="mb-4">
            <label className="block text-sm mb-1">Email:</label>
            <input
              type="email"
              name="email"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your Email Address"
              autoComplete="on"
              {...register("email", { required: true })}
            />
            {errors.email && (
              <span className="text-red-600 text-[10px] italic">
                Email is required
              </span>
            )}
          </div>
          <div className="mb-4">
            <label className="block text-sm mb-1">Password:</label>
            <input
              type="password"
              name="password"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your Password"
              autoComplete="on"
              {...register("password", { required: true })}
            />
            {errors.password && (
              <span className="text-red-600 text-[10px] italic">
                Password is required
              </span>
            )}
          </div>
          <div className="mb-4">
            <label className="block text-sm mb-1">Phone No:</label>
            <input
              type="text"
              name="phone"
              className="w-full px-3 py-2 border-b-2 text-sm rounded focus:outline-none focus:border-blue-500"
              placeholder="Enter your Phone Number"
              autoComplete="on"
              {...register("phone", { required: true })}
            />
            {errors.phone && (
              <span className="text-red-600 text-[10px] italic">
                Phone No is required
              </span>
            )}
          </div>
          <button
            className={`w-full mt-6 px-4 py-2 bg-blue-500 text-white font-bold rounded hover:bg-blue-700 focus:outline-none`}
            disabled={loading}
          >
            {loading ? "Loading..." : "Sign up"}
          </button>
          <div className="text-center text-sm mt-4">
            Already have an account?{" "}
            <Link to="/login" className="text-blue-500">
              Login
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

